import { __decorate } from "tslib";
import { Component, ViewChild } from '@angular/core';
import { CampeonatoBrasileiro } from 'src/app/enum/CampeonatoBrasileiro';
import { BrasaoTimeLista } from 'src/app/enum/BrasaoTimeLista';
import { DataLista } from 'src/app/enum/DataLista';
import { CopadoBrasil } from 'src/app/enum/CopadoBrasil';
import { Supercopa } from 'src/app/enum/Supercopa';
let TabelasComponent = class TabelasComponent {
    constructor(api, http, formBuilder, css) {
        this.api = api;
        this.http = http;
        this.formBuilder = formBuilder;
        this.css = css;
        this.ano = 2021;
        this.posicao = 0;
        this.page = 1;
        this.pageSize = 10;
        this.colecao = 20;
        this.brasaoTime = BrasaoTimeLista;
        this.botaoDataLista = DataLista;
        this.nomeJogadorCard = "neymar";
    }
    ngOnInit() {
        this.tipo = "campeonato-brasileiro";
        this.serieSelecionada = "serie-a";
        this.filtroTipo();
        this.buscarTabela();
        this.tabelaRecentes();
    }
    filtroTipo() {
        if (this.tipo == "campeonato-brasileiro") {
            this.seriesLista = CampeonatoBrasileiro;
        }
        if (this.tipo == "copa-brasil") {
            this.seriesLista = CopadoBrasil;
        }
        if (this.tipo == "supercopa") {
            this.seriesLista = Supercopa;
        }
    }
    async buscarTabela() {
        this.page = 1;
        this.TabelaPrincipalData = await this.http.get(`https://localhost:7126/api/Tabelas/tabela-principal/${this.ano}/${this.serieSelecionada}`).toPromise();
        this.mudaPagina();
        this.colecao = this.TabelaPrincipalData.length;
    }
    async tabelaRecentes() {
        this.tabelaJogosRecentes = await this.http.get(`https://localhost:7126/api/Tabelas/tabela-jogos-recentes`).toPromise();
    }
    setStyle() {
        this.css.setStyle(this.divHello.nativeElement, 'background', 'blue');
    }
    mudaPagina() {
        this.posicao = 10;
        this.TabelaPrincipal = this.TabelaPrincipalData
            .map((time, i) => (Object.assign({ id: i + 1 }, time)))
            .slice((this.page - 1) * this.pageSize, (this.page - 1) * this.pageSize + this.pageSize);
        this.setStyle();
    }
};
