import { __decorate } from "tslib";
import { Component } from '@angular/core';
import { SerieALista } from '../../enum/SerieALista';
let TabelasComponent = class TabelasComponent {
    constructor(api, http, formBuilder) {
        this.api = api;
        this.http = http;
        this.formBuilder = formBuilder;
        this.ano = 2021;
        this.posicao = 0;
        this.page = 1;
        this.pageSize = 10;
        this.colecao = 20;
    }
    ngOnInit() {
        this.tipo = "campeonato-brasileiro";
        this.serieSelecionada = "serie-a";
        this.filtroTipo();
        this.buscarTabela();
    }
    filtroTipo() {
        if (this.tipo == "campeonato-brasileiro") {
            this.seriesLista = SerieALista;
        }
        if (this.tipo == "copa-brasil") {
            this.seriesLista = [];
        }
    }
    async buscarTabela() {
        this.TabelaPrincipalData = await this.http.get(`https://localhost:7126/api/Tabelas/tabela-principal/${this.ano}/${this.serieSelecionada}`).toPromise();
        this.mudaPagina();
        this.colecao = this.TabelaPrincipalData.length;
        console.log('chegou');
    }
    mudaPagina() {
        this.posicao = 10;
        this.TabelaPrincipal = this.TabelaPrincipalData
            .map((time, i) => (Object.assign({ id: i + 1 }, time)))
            .slice((this.page - 1) * this.pageSize, (this.page - 1) * this.pageSize + this.pageSize);
    }
};
TabelasComponent = __decorate([
    Component({
        selector: 'app-tabelas',
        templateUrl: './tabelas.component.html',
        styleUrls: ['./tabelas.component.scss']
    })
], TabelasComponent);
export { TabelasComponent };
